import React, { useState, useCallback, useEffect, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useKeystrokeTracking } from '../hooks/useKeystrokeTracking';
import { useVersionHistory } from '../hooks/useVersionHistory';
import { saveDraft, getDraft, getAllDrafts, deleteDraft, createNewDraft } from '../utils/storage';
import { downloadPDF } from '../utils/pdfGenerator';
import '../styles/EnhancedTextEditor.css';

export const EnhancedTextEditor = ({ onStatsChange, onContentChange, onKeystrokesChange, onVersionsChange }) => {
  const [content, setContent] = useState('');
  const [currentDraft, setCurrentDraft] = useState(createNewDraft());
  const [drafts, setDrafts] = useState([]);
  const [showDraftsList, setShowDraftsList] = useState(false);
  const [lastSaved, setLastSaved] = useState(null);

  const quillRef = useRef(null);
  const autoSaveTimerRef = useRef(null);

  const { trackKeyDown, trackKeyUp, typingStats, keystrokes } = useKeystrokeTracking();
  const { autoSaveVersion, getVersionCount, versions } = useVersionHistory();

  // Quill modules configuration
  const modules = {
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline'],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      [{ 'size': ['small', false, 'large', 'huge'] }],
      ['clean']
    ],
  };

  const formats = [
    'header',
    'bold', 'italic', 'underline',
    'list', 'bullet',
    'size'
  ];

  // Load drafts on mount
  useEffect(() => {
    const allDrafts = getAllDrafts();
    setDrafts(allDrafts);

    // Load the last draft if exists
    if (allDrafts.length > 0) {
      const lastDraft = allDrafts[allDrafts.length - 1];
      setCurrentDraft(lastDraft);
      setContent(lastDraft.content || '');
    }
  }, []);

  // Auto-save every 30 seconds
  useEffect(() => {
    if (autoSaveTimerRef.current) {
      clearInterval(autoSaveTimerRef.current);
    }

    autoSaveTimerRef.current = setInterval(() => {
      handleSave();
    }, 30000); // 30 seconds

    return () => {
      if (autoSaveTimerRef.current) {
        clearInterval(autoSaveTimerRef.current);
      }
    };
  }, [content, currentDraft]);

  const handleSave = useCallback(() => {
    if (!content.trim()) return;

    const updatedDraft = {
      ...currentDraft,
      content: content,
      updatedAt: Date.now(),
    };

    if (saveDraft(updatedDraft)) {
      setLastSaved(new Date());
      setCurrentDraft(updatedDraft);
      setDrafts(getAllDrafts());
    }
  }, [content, currentDraft]);

  const handleChange = useCallback((value, delta, source, editor) => {
    const newContent = value;
    const plainText = editor.getText();

    setContent(newContent);
    autoSaveVersion(newContent);

    if (onContentChange) {
      onContentChange(plainText);
    }

    if (onKeystrokesChange) {
      onKeystrokesChange(keystrokes);
    }

    if (onVersionsChange) {
      onVersionsChange(versions);
    }

    if (onStatsChange) {
      const wordCount = plainText.trim() ? plainText.trim().split(/\s+/).length : 0;
      const charCount = plainText.length;
      onStatsChange({
        wordCount,
        charCount,
        versionCount: getVersionCount(),
        typingStats,
      });
    }
  }, [autoSaveVersion, getVersionCount, typingStats, keystrokes, versions, onStatsChange, onContentChange, onKeystrokesChange, onVersionsChange]);

  const handleLoadDraft = (draft) => {
    setCurrentDraft(draft);
    setContent(draft.content || '');
    setShowDraftsList(false);
  };

  const handleNewDraft = () => {
    const newDraft = createNewDraft();
    setCurrentDraft(newDraft);
    setContent('');
    setShowDraftsList(false);
  };

  const handleDeleteDraft = (draftId, e) => {
    e.stopPropagation();
    if (window.confirm('Are you sure you want to delete this draft?')) {
      deleteDraft(draftId);
      setDrafts(getAllDrafts());

      if (currentDraft.id === draftId) {
        handleNewDraft();
      }
    }
  };

  const handleDownloadPDF = () => {
    const wordCount = content.trim() ? content.trim().split(/\s+/).length : 0;
    const charCount = content.length;

    downloadPDF(content, `${currentDraft.title || 'document'}.pdf`, {
      title: currentDraft.title || 'My Document',
      includeMetadata: false,
      metadata: {
        wordCount,
        charCount,
      },
    });
  };

  const applyFormatting = (format) => {
    const textarea = textareaRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = content.substring(start, end);

    if (!selectedText) return;

    let formattedText = selectedText;
    let newContent = content;

    switch (format) {
      case 'bold':
        formattedText = `**${selectedText}**`;
        setIsBold(!isBold);
        break;
      case 'italic':
        formattedText = `*${selectedText}*`;
        setIsItalic(!isItalic);
        break;
      case 'h1':
        formattedText = `# ${selectedText}`;
        break;
      case 'h2':
        formattedText = `## ${selectedText}`;
        break;
      case 'h3':
        formattedText = `### ${selectedText}`;
        break;
      case 'bullet':
        formattedText = `- ${selectedText}`;
        break;
      case 'number':
        formattedText = `1. ${selectedText}`;
        break;
      default:
        break;
    }

    newContent = content.substring(0, start) + formattedText + content.substring(end);
    setContent(newContent);

    // Update textarea
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start, start + formattedText.length);
    }, 0);
  };

  const formatTime = (timestamp) => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;

    if (diff < 60000) return 'just now';
    if (diff < 3600000) return `${Math.floor(diff / 60000)}m ago`;
    if (diff < 86400000) return `${Math.floor(diff / 3600000)}h ago`;
    return date.toLocaleDateString();
  };

  return (
    <div className="enhanced-editor-card">
      <div className="editor-header">
        <div className="header-left">
          <input
            type="text"
            className="document-title"
            value={currentDraft.title}
            onChange={(e) => setCurrentDraft({ ...currentDraft, title: e.target.value })}
            placeholder="Untitled Document"
          />
          {lastSaved && (
            <span className="last-saved">Saved {formatTime(lastSaved)}</span>
          )}
        </div>
        <div className="header-actions">
          <button className="btn-icon-action" onClick={() => setShowDraftsList(!showDraftsList)} title="My Drafts">
            üìÅ
          </button>
          <button className="btn-icon-action" onClick={handleNewDraft} title="New Draft">
            ‚ûï
          </button>
          <button className="btn-icon-action" onClick={handleSave} title="Save">
            üíæ
          </button>
          <button className="btn-icon-action" onClick={handleDownloadPDF} title="Download as PDF">
            üì•
          </button>
        </div>
      </div>

      {showDraftsList && (
        <div className="drafts-dropdown">
          <div className="drafts-header">
            <h4>My Drafts ({drafts.length})</h4>
            <button onClick={() => setShowDraftsList(false)}>‚úï</button>
          </div>
          <div className="drafts-list">
            {drafts.length === 0 ? (
              <div className="no-drafts">No saved drafts</div>
            ) : (
              drafts.map(draft => (
                <div
                  key={draft.id}
                  className={`draft-item ${draft.id === currentDraft.id ? 'active' : ''}`}
                  onClick={() => handleLoadDraft(draft)}
                >
                  <div className="draft-info">
                    <div className="draft-title">{draft.title || 'Untitled'}</div>
                    <div className="draft-meta">
                      {formatTime(draft.updatedAt)} ‚Ä¢ {draft.content?.length || 0} chars
                    </div>
                  </div>
                  <button
                    className="delete-draft"
                    onClick={(e) => handleDeleteDraft(draft.id, e)}
                    title="Delete"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      )}

      <div className="editor-toolbar">
        <div className="toolbar-group">
          <button
            className={`toolbar-btn ${isBold ? 'active' : ''}`}
            onClick={() => applyFormatting('bold')}
            title="Bold (Markdown: **text**)"
          >
            <strong>B</strong>
          </button>
          <button
            className={`toolbar-btn ${isItalic ? 'active' : ''}`}
            onClick={() => applyFormatting('italic')}
            title="Italic (Markdown: *text*)"
          >
            <em>I</em>
          </button>
        </div>

        <div className="toolbar-group">
          <button className="toolbar-btn" onClick={() => applyFormatting('h1')} title="Heading 1">
            H1
          </button>
          <button className="toolbar-btn" onClick={() => applyFormatting('h2')} title="Heading 2">
            H2
          </button>
          <button className="toolbar-btn" onClick={() => applyFormatting('h3')} title="Heading 3">
            H3
          </button>
        </div>

        <div className="toolbar-group">
          <button className="toolbar-btn" onClick={() => applyFormatting('bullet')} title="Bullet List">
            ‚Ä¢
          </button>
          <button className="toolbar-btn" onClick={() => applyFormatting('number')} title="Numbered List">
            1.
          </button>
        </div>

        <div className="toolbar-group">
          <select
            className="font-size-select"
            value={fontSize}
            onChange={(e) => setFontSize(Number(e.target.value))}
          >
            <option value={12}>12px</option>
            <option value={14}>14px</option>
            <option value={16}>16px</option>
            <option value={18}>18px</option>
            <option value={20}>20px</option>
          </select>
        </div>
      </div>

      <div className="editor-subtitle">
        Your typing patterns are being tracked to ensure authenticity
      </div>

      <textarea
        ref={textareaRef}
        className="enhanced-textarea"
        value={content}
        onChange={handleChange}
        onKeyDown={trackKeyDown}
        onKeyUp={trackKeyUp}
        placeholder="Start writing your content here..."
        style={{ fontSize: `${fontSize}px` }}
        spellCheck
      />
    </div>
  );
};
